<?phpnamespace Nemundo\Roundshot\App\Container\Data\RoundshotContainerItem;class RoundshotContainerItemReader extends \Nemundo\Model\Reader\ModelDataReader {/*** @var RoundshotContainerItemModel*/public $model;public function __construct() {parent::__construct();$this->model = new RoundshotContainerItemModel();}/*** @return RoundshotContainerItemRow[]*/public function getData() {$list = [];foreach (parent::getData() as $dataRow) {$row = $this->getModelRow($dataRow);$list[] = $row;}return $list;}/*** @return RoundshotContainerItemRow*/public function getRow() {$dataRow = parent::getRow();$row = $this->getModelRow($dataRow);return $row;}/*** @return RoundshotContainerItemRow*/public function getRowById($id) {return parent::getRowById($id);}private function getModelRow($dataRow) {$row = new RoundshotContainerItemRow($dataRow, $this->model);$row->model = $this->model;return $row;}}