<?phpnamespace Nemundo\Roundshot\App\Timelapse\Data\Timelapse;class TimelapseReader extends \Nemundo\Model\Reader\ModelDataReader {/*** @var TimelapseModel*/public $model;public function __construct() {parent::__construct();$this->model = new TimelapseModel();}/*** @return TimelapseRow[]*/public function getData() {$list = [];foreach (parent::getData() as $dataRow) {$row = $this->getModelRow($dataRow);$list[] = $row;}return $list;}/*** @return TimelapseRow*/public function getRow() {$dataRow = parent::getRow();$row = $this->getModelRow($dataRow);return $row;}/*** @return TimelapseRow*/public function getRowById($id) {return parent::getRowById($id);}private function getModelRow($dataRow) {$row = new TimelapseRow($dataRow, $this->model);$row->model = $this->model;return $row;}}