<?phpnamespace Nemundo\Roundshot\App\Timelapse\Data\TimelapseJob;class TimelapseJob extends \Nemundo\Model\Data\AbstractModelData {/*** @var TimelapseJobModel*/protected $model;/*** @var \Nemundo\Core\Type\DateTime\Date*/public $date;/*** @var string*/public $roundshotId;/*** @var bool*/public $done;public function __construct() {parent::__construct();$this->model = new TimelapseJobModel();$this->date = new \Nemundo\Core\Type\DateTime\Date();}public function save() {$property = new \Nemundo\Model\Data\Property\DateTime\DateDataProperty($this->model->date, $this->typeValueList);$property->setValue($this->date);$this->typeValueList->setModelValue($this->model->roundshotId, $this->roundshotId);$this->typeValueList->setModelValue($this->model->done, $this->done);$id = parent::save();return $id;}}