<?phpnamespace Nemundo\Roundshot\App\Timelapse\Data\TimelapseJob;use Nemundo\Model\Data\AbstractModelUpdate;class TimelapseJobUpdate extends AbstractModelUpdate {/*** @var TimelapseJobModel*/public $model;/*** @var \Nemundo\Core\Type\DateTime\Date*/public $date;/*** @var string*/public $roundshotId;/*** @var bool*/public $done;public function __construct() {parent::__construct();$this->model = new TimelapseJobModel();$this->date = new \Nemundo\Core\Type\DateTime\Date();}public function update() {$property = new \Nemundo\Model\Data\Property\DateTime\DateDataProperty($this->model->date, $this->typeValueList);$property->setValue($this->date);$this->typeValueList->setModelValue($this->model->roundshotId, $this->roundshotId);$this->typeValueList->setModelValue($this->model->done, $this->done);parent::update();}}