<?phpnamespace Nemundo\Roundshot\Com\Form;use Nemundo\Admin\Com\Button\AdminSearchButton;use Nemundo\Com\FormBuilder\SearchForm;use Nemundo\Package\Bootstrap\Layout\Grid\BootstrapRow;use Nemundo\Package\Bootstrap\FormElement\BootstrapDatePicker;use Nemundo\Roundshot\Com\ListBox\RoundshotListBox;use Nemundo\Roundshot\Parameter\DateParameter;use Nemundo\Roundshot\Parameter\RoundshotParameter;class RoundshotSearchForm extends SearchForm{    protected function loadContainer()    {        parent::loadContainer();        $formRow=new BootstrapRow($this);        $roundshot = new RoundshotListBox($formRow);        $roundshot->name=(new RoundshotParameter())->getParameterName();        $roundshot->searchMode=true;        $date= new BootstrapDatePicker($formRow);        $date->label='Date';        $date->name=(new DateParameter())->getParameterName();        $date->searchMode=true;        new AdminSearchButton($formRow);    }}