<?phpnamespace Nemundo\Roundshot\Com\ListBox;use Nemundo\Package\Bootstrap\FormElement\BootstrapListBox;use Nemundo\Roundshot\Data\Roundshot\RoundshotReader;class RoundshotListBox extends BootstrapListBox{    public function getContent()    {        $this->label = 'Roundshot';        $reader = new RoundshotReader();        $reader->addOrder($reader->model->roundshot);        foreach ($reader->getData() as $roundshotRow) {            $this->addItem($roundshotRow->id, $roundshotRow->roundshot);        }        return parent::getContent();    }}