<?phpnamespace Nemundo\Roundshot\Content\Roundshot;use Nemundo\Admin\Com\Table\AdminClickableTable;use Nemundo\Com\TableBuilder\TableHeader;use Nemundo\Content\Parameter\ContentParameter;use Nemundo\Content\View\AbstractContentView;use Nemundo\Package\Bootstrap\Pagination\BootstrapPagination;use Nemundo\Package\Bootstrap\Table\BootstrapClickableTableRow;use Nemundo\Roundshot\Content\Webcam\RoundshotWebcamContentType;use Nemundo\Roundshot\Data\Roundshot\RoundshotPaginationReader;class RoundshotContentView extends AbstractContentView{    /**     * @var RoundshotWidgetContentType     */    public $contentType;    protected function loadView()    {        $this->viewName = 'default';        $this->viewId = '7d319982-4ce3-4150-af8a-740e591dc5fb';        $this->defaultView = true;    }    public function getContent()    {        // map view        // list view// ChildView (reloading, link)        $table = new AdminClickableTable($this);        $roundshotReader = new RoundshotPaginationReader();        $roundshotReader->addOrder($roundshotReader->model->roundshot);        $header = new TableHeader($table);        $header->addText($roundshotReader->model->roundshot->label);        $header->addText($roundshotReader->model->geoCoordinate->altitude->label);        foreach ($roundshotReader->getData() as $roundshotRow) {            $row = new BootstrapClickableTableRow($table);            $row->addText($roundshotRow->roundshot);            $row->addText($roundshotRow->geoCoordinate->altitude);            $site = clone($this->redirectSite);            $contentType = new RoundshotWebcamContentType($roundshotRow->id);            $site->addParameter(new ContentParameter($contentType->getContentId()));            $row->addClickableSite($site);            //$row->addClickableSite($this->getContentRedirectSite($contentType->getContentId()));        }        $pagination = new BootstrapPagination($this);        $pagination->paginationReader = $roundshotReader;        return parent::getContent();    }}