<?phpnamespace Nemundo\Roundshot\Content\Webcam;use Nemundo\Content\Index\Tree\Com\Form\AbstractContentSearchForm;use Nemundo\Roundshot\Com\ListBox\RoundshotListBox;class RoundshotContentSearchForm extends AbstractContentSearchForm{    /**     * @var RoundshotWebcamContentType     */    public $contentType;    /**     * @var RoundshotListBox     */    private $roundshot;    public function getContent()    {        $this->roundshot = new RoundshotListBox($this);        return parent::getContent();    }    protected function onSubmit()    {        $contentType = new RoundshotWebcamContentType($this->roundshot->getValue());        $this->saveTree($contentType);    }}