<?phpnamespace Nemundo\Roundshot\Content\Webcam;use Nemundo\Com\Html\Hyperlink\UrlHyperlink;use Nemundo\Content\View\AbstractContentView;use Nemundo\Html\Formatting\Small;use Nemundo\Package\Bootstrap\Image\BootstrapResponsiveImage;use Nemundo\Package\Bootstrap\Layout\Grid\BootstrapRow;class WebcamContentView extends AbstractContentView{    /**     * @var RoundshotWebcamContentType     */    public $contentType;    protected function loadView()    {        $this->viewName = 'default';        $this->viewId = 'c5efd7e6-52ca-43f7-9190-26e5cce591f8';        $this->defaultView = true;    }    public function getContent()    {        $roundshotRow = $this->contentType->getDataRow();        $row = new BootstrapRow($this);        $link = new UrlHyperlink($row);        $link->url = $roundshotRow->url;        $link->openNewWindow = true;        $img = new BootstrapResponsiveImage($link);        $img->src = $roundshotRow->url . 'cams/' . $roundshotRow->roundshotNumber . '/thumbnail';        $row = new BootstrapRow($this);        $small = new Small($row);        $small->content = 'Source Roundshot';        return parent::getContent();    }}