<?phpnamespace Nemundo\Roundshot\Content\RoundshotAttachment;use Nemundo\Content\Form\AbstractContentForm;use Nemundo\Content\Index\Tree\Builder\TreeBuilder;use Nemundo\Roundshot\Com\ListBox\RoundshotListBox;use Nemundo\Roundshot\Content\Roundshot\RoundshotWebcamContentType;class RoundshotAttachmentContentForm extends AbstractContentForm{    /**     * @var RoundshotAttachmentContentType     */    public $contentType;    /**     * @var RoundshotListBox     */    private $roundshot;    public function getContent()    {        $this->roundshot = new RoundshotListBox($this);        return parent::getContent();    }    protected function loadUpdateForm()    {        $roundshotRow = $this->contentType->getDataRow();        $this->roundshot->value = $roundshotRow->id;    }    public function onSubmit()    {        $writer = new TreeBuilder();        $writer->parentId = $this->contentType->parentId;        $writer->childId = (new RoundshotWebcamContentType($this->roundshot->getValue()))->getContentId();        $writer->write();        /*$this->contentType->roundshotId = $this->roundshot->getValue();        $this->contentType->saveType();*/    }}