<?phpnamespace Nemundo\Roundshot\Content\RoundshotAttachment;use Nemundo\Admin\Com\Widget\AdminWidget;use Nemundo\Com\Html\Hyperlink\UrlHyperlink;use Nemundo\Content\View\AbstractContentView;class RoundshotAttachmentContentView extends AbstractContentView{    /**     * @var RoundshotAttachmentContentType     */    public $contentType;    public function getContent()    {        $widget = new AdminWidget($this);        $roundshotRow = $this->contentType->getDataRow();        $hyperlink = new UrlHyperlink($widget);        $hyperlink->url = $roundshotRow->url;        $hyperlink->content = $roundshotRow->roundshot;        $hyperlink->openNewWindow = true;        return parent::getContent();    }}