<?phpnamespace Nemundo\Roundshot\Data\Roundshot;class RoundshotReader extends \Nemundo\Model\Reader\ModelDataReader {/*** @var RoundshotModel*/public $model;public function __construct() {parent::__construct();$this->model = new RoundshotModel();}/*** @return RoundshotRow[]*/public function getData() {$list = [];foreach (parent::getData() as $dataRow) {$row = $this->getModelRow($dataRow);$list[] = $row;}return $list;}/*** @return RoundshotRow*/public function getRow() {$dataRow = parent::getRow();$row = $this->getModelRow($dataRow);return $row;}/*** @return RoundshotRow*/public function getRowById($id) {return parent::getRowById($id);}private function getModelRow($dataRow) {$row = new RoundshotRow($dataRow, $this->model);$row->model = $this->model;return $row;}}