<?phpnamespace Paranautik\App\Roundshot\Download;use Nemundo\Core\Base\AbstractBase;use Paranautik\App\Roundshot\Data\RoundshotImage\RoundshotImageReader;use Paranautik\App\Roundshot\Data\RoundshotImage\RoundshotImageUpdate;class ImageDownload extends AbstractBase{    /**     * @var string     */    public $imageId;    public function download()    {        $imageRow = (new RoundshotImageReader())->getRowById($this->imageId);        $update = new RoundshotImageUpdate();        $update->download = true;        $update->image->fromUrl($imageRow->imageUrl);        $update->updateById($this->imageId);    }}