<?phpnamespace Nemundo\Roundshot\Kml;use Nemundo\Com\Html\Hyperlink\UrlHyperlink;use Nemundo\Core\Type\Text\Text;use Nemundo\Geo\Kml\Container\KmlFolder;use Nemundo\Geo\Kml\Object\KmlMarker;use Nemundo\Html\Container\HtmlContainer;use Paranautik\App\Roundshot\Data\Roundshot\RoundshotReader;class RoundshotKmlFolder extends KmlFolder{    public function getContent()    {        $this->label = 'Roundshot';        $reader = new RoundshotReader();        foreach ($reader->getData() as $roundshotRow) {            $titleText = (new Text($roundshotRow->title))->replace('&', '')->getValue();            //$titleText = $roundshotRow->title;            $description = new HtmlContainer();  // new HtmlContainerList();            //$title = new H3($description);            //$title->content = $titleText;            $link = new UrlHyperlink($description);            $link->url = $roundshotRow->url;            $link->content = $roundshotRow->title;          $point = new KmlMarker($this);  // new Placemark($this);  // $this);  // new KmlPlacemark($this);          $point->coordinate = $roundshotRow->coordinate;          $point->label = $titleText;          $point->description = $description->getContent();      }        return parent::getContent();    }}