<?phpnamespace Nemundo\Roundshot\Page;use Nemundo\Admin\Com\Button\AdminSearchButton;use Nemundo\Admin\Com\Table\AdminBootstrapTable;use Nemundo\Com\FormBuilder\SearchForm;use Nemundo\Com\Html\Hyperlink\UrlHyperlink;use Nemundo\Com\TableBuilder\TableRow;use Nemundo\Package\Bootstrap\FormElement\BootstrapDatePicker;use Nemundo\Package\Bootstrap\Layout\Grid\BootstrapRow;use Nemundo\Roundshot\Com\ListBox\RoundshotListBox;use Nemundo\Roundshot\Content\Webcam\RoundshotWebcamContentType;use Nemundo\Roundshot\Reader\RoundshotImageJsonReader;use Nemundo\Roundshot\Template\RoundshotTemplate;class RoundshotJsonImagePage extends RoundshotTemplate  // DesktopTemplate{    public function getContent()    {        $form = new SearchForm($this);        $formRow = new BootstrapRow($form);        $roundshot = new RoundshotListBox($formRow);        $roundshot->searchMode = true;        $date = new BootstrapDatePicker($formRow);        $date->label = 'Date';        $date->searchMode = true;        new AdminSearchButton($formRow);        if ($date->hasValue()) {            $roundshotRow = (new RoundshotWebcamContentType($roundshot->getValue()))->getDataRow();            $table = new AdminBootstrapTable($this);            $imageReader = new RoundshotImageJsonReader();            $imageReader->date = $date->getDateValue();            $imageReader->roundshotUrl = $roundshotRow->url;            foreach ($imageReader->getData() as $imageRow) {                $row = new TableRow($table);                $row->addText($imageRow->dateTime->getIsoDate());                $row->addText($imageRow->dateTime->getIsoTime());                $link = new UrlHyperlink($row);                $link->content = $imageRow->imageUrl;                $link->url = $imageRow->imageUrl;                $link->openNewWindow = true;            }        }        return parent::getContent();    }}