<?phpnamespace Nemundo\Roundshot;use Nemundo\FrameworkProject;use Nemundo\Project\AbstractProject;class RoundshotProject extends AbstractProject{    protected function loadProject()    {        $this->project = 'Roundshot';        $this->projectName = 'roundshot';        $this->path = __DIR__;        $this->namespace = __NAMESPACE__;        $this->modelPath = __DIR__ . DIRECTORY_SEPARATOR . '..' . DIRECTORY_SEPARATOR . 'model' . DIRECTORY_SEPARATOR;        $this->addDependency(new FrameworkProject());    }}