<?phpnamespace Nemundo\Roundshot\Script;use Nemundo\App\Script\Type\AbstractConsoleScript;use Nemundo\Content\Data\ContentAction\ContentActionReader;use Nemundo\Roundshot\Content\Webcam\RoundshotWebcamContentType;use Nemundo\Roundshot\Data\Roundshot\RoundshotReader;class RoundshotCleanScript extends AbstractConsoleScript{    protected function loadScript()    {        $this->scriptName = 'roundshot-clean';    }    public function run()    {        $actionReader = new ContentActionReader();        $reader = new RoundshotReader();        foreach ($reader->getData() as $roundshotRow) {            $content = new RoundshotWebcamContentType($roundshotRow->id);            foreach ($actionReader->getData() as $actionRow) {                $action = $actionRow->getAction();                $action->deleteAction($content);            }            $content->deleteType();        }    }}