<?phpnamespace Nemundo\Roundshot\Site;use Nemundo\Geo\Kml\Site\AbstractKmlSite;use Nemundo\Roundshot\Kml\RoundshotKmlDocument;use Nemundo\Web\Site\AbstractSite;class RoundshotKmlSite extends AbstractKmlSite{    /**     * @var RoundshotKmlSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Roundshot Kml';        $this->url = 'roundshot-kml';        $this->menuActive = false;        RoundshotKmlSite::$site = $this;    }    public function loadContent()    {        (new RoundshotKmlDocument())->render();    }}