<?phpnamespace Nemundo\Srf\App\Livestream\Install;use Nemundo\App\Application\Setup\ApplicationSetup;use Nemundo\App\Application\Type\Install\AbstractInstall;use Nemundo\App\Script\Setup\ScriptSetup;use Nemundo\App\WebService\Setup\ServiceRequestSetup;use Nemundo\Content\Application\ContentApplication;use Nemundo\Content\Setup\ContentTypeSetup;use Nemundo\Model\Setup\ModelCollectionSetup;use Nemundo\Srf\App\Livestream\Application\SrfLivestreamApplication;use Nemundo\Srf\App\Livestream\Content\LivestreamWidget\LivestreamWidgetContentType;use Nemundo\Srf\App\Livestream\Content\RadioLivestream\RadioLivestreamContentType;use Nemundo\Srf\App\Livestream\Data\LivestreamModelCollection;use Nemundo\Srf\App\Livestream\Script\LivestreamImportScript;use Nemundo\Srf\App\Livestream\Service\LivestreamService;class LivestreamInstall extends AbstractInstall{    public function install()    {        (new ContentApplication())->installApp();  //->setAppMenuActive();        (new ApplicationSetup())            ->addApplication(new SrfLivestreamApplication());        (new ModelCollectionSetup())            ->addCollection(new LivestreamModelCollection());        (new ContentTypeSetup(new SrfLivestreamApplication()))            ->addContentType(new RadioLivestreamContentType())            ->addContentType(new LivestreamWidgetContentType());        (new LivestreamWidgetContentType())->saveType();        (new ScriptSetup(new SrfLivestreamApplication()))            ->addScript(new LivestreamImportScript());        (new ServiceRequestSetup(new SrfLivestreamApplication()))            ->addService(new LivestreamService());    }}