<?phpnamespace Nemundo\Srf\Com\Player;use Nemundo\Core\Http\Url\UrlBuilder;use Nemundo\Html\Iframe\Iframe;use Nemundo\Web\Parameter\UrlParameter;class SrfPlayer extends Iframe{    /**     * @var string     */    public $urn;    /**     * @var bool     */    public $autoPlay = false;    /**     * @var bool     */    //public $legacy = false;    public function getContent()    {        /*$this->width = '100%';        $this->height= '100%';*/        $this->addAttributeWithoutValue('allowfullscreen');        //$url = new UrlBuilder('//tp.srgssr.ch/p/embed');        $url = new UrlBuilder('https://www.srf.ch/play/embed');        $url->addRequestValue('urn', $this->urn);        if ($this->autoPlay) {            $url->addRequestValue('autoplay','true');        }        /*if ($this->legacy) {            $url->addRequestValue('legacy','true');        }*/        // show more video        //https://www.srf.ch/play/embed?urn=urn:srf:video:99099d42-1b39-42e4-a5ea-2542bafdd2f0&subdivisions=false" allowfullscreen allow="geolocation *; autoplay; encrypted-media"        $this->src = $url->getUrlWithoutEncoding();  // getUrl() getUrlWithoutEncoding();        //$this->src = $url->getUrl();        return parent::getContent();    }}