<?phpnamespace Nemundo\Srf\Import\Show;use Nemundo\Core\Base\AbstractImport;use Nemundo\Srf\Data\Show\ShowCount;abstract class AbstractShowImport extends AbstractImport{    protected function existsItem($srfId)    {        $value = false;        $count = new ShowCount();        $count->filter->andEqual($count->model->srfId, $srfId);        if ($count->getCount() === 1) {            $value = true;        }        return $value;    }}