<?phpnamespace Nemundo\Srf\Import\Show;use Nemundo\Srf\Config\BusinessUnit;use Nemundo\Srf\Data\Show\Show;use Nemundo\Srf\MediaType\RadioMediaType;use Nemundo\Srf\Reader\Show\RadioShowJsonReader;class RadioShowImport extends AbstractShowImport{    public function importData()    {        $reader = new RadioShowJsonReader();        $reader->businessUnit = BusinessUnit::SRF;        foreach ($reader->getData() as $item) {            if (!$this->existsItem($item->srfId)) {                $data = new Show();                //$data->updateOnDuplicate = true;                $data->mediaTypeId = (new RadioMediaType())->id;                $data->show = $item->show;                $data->description = $item->description;                $data->srfId = $item->srfId;                $data->image->fromUrl($item->imageUrl);                $data->crawler = false;                $data->save();            }            /*$id=new ShowId();            $id->filter->andEqual($id->model->srfId, $item->srfId);            $dataId=$id->getId();*/            /*            $type = new RadioShowContentType($dataId);            (new IndexBuilder())->buildIndex($type);*/            /*            $data = new Show();            $data->updateOnDuplicate = true;            $data->mediaTypeId = (new RadioMediaType())->id;            $data->show = $item->show;            $data->srfId = $item->id;            $data->save();*/            //$img->src=$item->imageUrl;        }    }}