<?phpnamespace Nemundo\Srf\Page\Admin;use Nemundo\Admin\Com\Table\AdminTable;use Nemundo\Admin\Com\Table\Row\AdminTableRow;use Nemundo\Admin\Com\Title\AdminSubtitle;use Nemundo\Admin\Com\Title\AdminTitle;use Nemundo\Com\Html\Hyperlink\SiteHyperlink;use Nemundo\Com\TableBuilder\TableHeader;use Nemundo\Content\App\Dashboard\Com\Form\DashboardSaveForm;use Nemundo\Core\Time\Second;use Nemundo\Db\Sql\Order\SortOrder;use Nemundo\Html\Block\ContentDiv;use Nemundo\Html\Block\Div;use Nemundo\Html\Block\Hr;use Nemundo\Html\Paragraph\Paragraph;use Nemundo\Package\Bootstrap\Layout\BootstrapTwoColumnLayout;use Nemundo\Package\Bootstrap\Listing\BootstrapHyperlinkListGroup;use Nemundo\Package\Bootstrap\Listing\BootstrapSiteListGroup;use Nemundo\Srf\Content\TvEpisode\TvEpisodeContentType;use Nemundo\Srf\Content\TvShow\TvShowContentType;use Nemundo\Srf\Data\Episode\EpisodeReader;use Nemundo\Srf\Data\Show\ShowReader;use Nemundo\Srf\Parameter\EpisodeParameter;use Nemundo\Srf\Parameter\ShowParameter;use Nemundo\Srf\Site\Admin\CrawlerActiveSite;use Nemundo\Srf\Site\SrfEpisodeSite;use Nemundo\Srf\Site\SrfSite;use Nemundo\Srf\Template\SrfTemplate;class SrfAdminPage extends SrfTemplate{    public function getContent()    {        $table=new AdminTable($this);        $showReader = new ShowReader();        $showReader->model->loadMediaType();        $showReader->addOrder($showReader->model->show);        $header=new TableHeader($table);        $header->addText($showReader->model->show->label);        $header->addText($showReader->model->description->label);        $header->addText($showReader->model->srfId->label);        $header->addText($showReader->model->crawler->label);        foreach ($showReader->getData() as $showRow) {            $tableRow=new AdminTableRow($table);            $tableRow->addText($showRow->show);            $tableRow->addText($showRow->description);            $tableRow->addText($showRow->srfId);            $tableRow->addYesNo($showRow->crawler);            $site=clone(CrawlerActiveSite::$site);            $site->addParameter(new ShowParameter($showRow->id));            $tableRow->addSite($site);        }        return parent::getContent();    }}