<?phpnamespace Nemundo\Srf\Page;use Nemundo\Admin\Com\Table\AdminBootstrapTable;use Nemundo\Admin\Com\Table\AdminTableHeader;use Nemundo\Admin\Com\Table\Row\AdminTableRow;use Nemundo\Package\Bootstrap\Image\BootstrapResponsiveImage;use Nemundo\Srf\Data\Show\ShowReader;use Nemundo\Srf\Template\SrfTemplate;class ShowPage extends SrfTemplate{    public function getContent()    {        $table = new AdminBootstrapTable($this);        $reader = new ShowReader();        $reader->model->loadMediaType();        $header = new AdminTableHeader($table);        $header->addEmpty();        $header->addText($reader->model->show->label);        $header->addText($reader->model->description->label);        $header->addText($reader->model->mediaType->media->label);        $header->addText($reader->model->srfId->label);        $header->addText($reader->model->crawler->label);        foreach ($reader->getData() as $showRow) {            $row = new AdminTableRow($table);            $img = new BootstrapResponsiveImage($row);            $img->src = $showRow->image->getUrl();            $img->width = 300;            $row->addText($showRow->show);            $row->addText($showRow->description);            $row->addText($showRow->mediaType->media);            $row->addText($showRow->srfId);            $row->addYesNo($showRow->crawler);        }        return parent::getContent();    }}