<?phpnamespace Nemundo\Srf\Site\Admin;use Nemundo\Core\Http\Url\UrlReferer;use Nemundo\Srf\Data\Show\ShowUpdate;use Nemundo\Srf\Page\Admin\SrfAdminPage;use Nemundo\Srf\Page\SrfPage;use Nemundo\Srf\Parameter\ShowParameter;use Nemundo\Web\Site\AbstractSite;class CrawlerActiveSite extends AbstractSite{    /**     * @var CrawlerActiveSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Active';        $this->url = 'crawler-active';        CrawlerActiveSite::$site=$this;    }    public function loadContent()    {        $update=new ShowUpdate();        $update->crawler=true;        $update->updateById((new ShowParameter())->getValue());        (new UrlReferer())->redirect();       // (new SrfAdminPage())->render();    }}