<?phpnamespace Nemundo\Srf\Site;use Nemundo\Srf\Page\ShowPage;use Nemundo\Web\Site\AbstractSite;class ShowSite extends AbstractSite{    /**     * @var ShowSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Show';        $this->url = 'show';        ShowSite::$site=$this;    }    public function loadContent()    {        (new ShowPage())->render();    }}