<?phpnamespace Nemundo\Srf\Token;use Nemundo\Core\Base\AbstractBase;use Nemundo\Core\Debug\Debug;class SrfToken extends AbstractBase{    public function __construct() {        SrfToken::$clientId = 'ezM7Ba881g6dTnPdzY1IhAux5wNX9Cql';        SrfToken::$clientSecret= 'jUQbFGs1e1e9bvPw';    }    /**     * @var string     */    public static $clientId;    /**     * @var string     */    public static $clientSecret;    private static $token;    public function getToken()    {        /*curl_setopt($ch, CURLOPT_SSL_VERIFYHOST, false);        curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false);*/        if (SrfToken::$token == null) {            $url = 'https://api.srgssr.ch/oauth/v1/accesstoken?grant_type=client_credentials';            $curl = curl_init($url);            curl_setopt($curl, CURLOPT_FOLLOWLOCATION, false);            curl_setopt($curl, CURLOPT_HEADER, false);            curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);            curl_setopt($curl, CURLOPT_POST, true);            curl_setopt($curl, CURLOPT_POSTFIELDS, rawurldecode(http_build_query(array(                'client_id' => SrfToken::$clientId,                'client_secret' => SrfToken::$clientSecret,                'grant_type' => 'authorization_code'            ))));            $json = json_decode(curl_exec($curl), true);            if (curl_errno($curl)) {                //$error_msg = curl_error($curl);                (new Debug())->write(curl_error($curl));            }            if (isset($json['access_token'])) {                SrfToken::$token = $json['access_token'];                //$accessToken = $json['access_token'];            } else {                (new Debug())->write($json);                exit;            }        }        return SrfToken::$token;        //return $accessToken;    }}