<?phpnamespace Nemundo\WebLog\Cookie;use Nemundo\Core\Http\Cookie\AbstractCookie;use Nemundo\Core\Random\UniqueId;// TrackingCode (external class)class TrackingCookie extends AbstractCookie{    protected function loadCookie()    {        $this->cookieName = 'weblog';        if (!$this->exists()) {            $trackingCode = (new UniqueId())->getUniqueId();            $this->defaultValue=$trackingCode;            $this->setValue($trackingCode);            // save        }        //$this->getValue()    }}