<?phpnamespace Nemundo\WebLog\Site;use Nemundo\Web\Site\AbstractSite;use Nemundo\WebLog\Page\WebLogPage;class WebLogSite extends AbstractSite{    /**     * @var WebLogSite     */    public static $site;    protected function loadSite()    {        $this->title = 'Web Log';        $this->url = 'web-log';        WebLogSite::$site = $this;        new UrlLogSite($this);        new BrowserAgentSite($this);        new SlowPageSite($this);    }    public function loadContent()    {        (new WebLogPage())->render();    }}